import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Bike } from '../../models/bike';
import { BikeService } from '../../services/bike.service';

@Component({
  selector: 'app-bike-list',
  templateUrl: './bike-list.component.html',
  styleUrls: ['./bike-list.component.css']
})
export class BikeListComponent implements OnInit {
  bikes: Bike[] = [];
  loading = false;
  error = '';

  constructor(private bikeService: BikeService, private router: Router) {}

  ngOnInit(): void {
    this.loadBikes();
  }

  loadBikes(): void {
    this.loading = true;
    this.bikeService.getBikes().subscribe({
      next: bikes => {
        this.bikes = bikes;
        this.loading = false;
      },
      error: err => {
        this.error = 'Fehler beim Laden der Räder.';
        this.loading = false;
      }
    });
  }

  openDetail(id: number): void {
    this.router.navigate(['/bikes', id]);
  }

  addBike(): void {
    this.router.navigate(['/bikes/add']);
  }
}
<div class="flex justify-between items-center mb-6">
  <h2 class="text-2xl font-bold text-primary dark:text-text-dark">Meine Räder</h2>
  <button
    class="bg-accent hover:bg-accent/90 disabled:opacity-60 text-white font-semibold rounded-lg px-4 py-2 text-sm cursor-pointer transition-colors duration-150"
    (click)="addBike()">
    + Rad hinzufügen
  </button>
</div>

<div *ngIf="loading" class="text-center mt-10 text-gray-500">Lädt...</div>
<div *ngIf="error" class="text-center mt-10 text-error">{{ error }}</div>

<div *ngIf="!loading && bikes.length === 0 && !error" class="text-center mt-16 text-gray-500">
  Noch keine Räder vorhanden. Füge dein erstes Rad hinzu!
</div>

<div class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
  <div
    class="bg-white dark:bg-bg-dark rounded-xl p-5 shadow-sm cursor-pointer transition-all duration-150 border-l-4 border-accent hover:-translate-y-1 hover:shadow-md"
    *ngFor="let bike of bikes"
    (click)="openDetail(bike.id)">
    <div class="text-xs font-bold uppercase tracking-widest text-accent mb-1">{{ bike.kategorie }}</div>
    <h3 class="text-lg font-semibold text-primary dark:text-text-dark mt-0 mb-4">{{ bike.name }}</h3>
    <div class="flex justify-between items-baseline border-t border-gray-100 dark:border-gray-700 pt-3">
      <span class="text-xs text-gray-400">Kilometerstand</span>
      <span class="text-base font-bold text-primary dark:text-text-dark">{{ bike.kilometerstand | number:'1.0-0' }} km</span>
    </div>
  </div>
</div>
